    private void txtUtahValueKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtUtahValueKeyTyped
        // TODO add your handling code here:
        if(!txtUtahValue.getText().isEmpty()){
            btmCalculate.setEnabled(true);
        }
        if(txtUtahValue.getText().isEmpty()){
            btmCalculate.setEnabled(false);
        }
    }//GEN-LAST:event_txtUtahValueKeyTyped

    private void btmCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btmCalculateActionPerformed
        // TODO add your handling code here:
        USTax utahTax = USTax.getInstance(0.0485F);
        utahTax.setTaxValue(0.0485F);
        USTaxController utahTaxTotal = new USTaxController();
        lbTotal.setText(Float.toString(utahTaxTotal.salesTotal(Float.parseFloat(txtUtahValue.getText()), utahTax.getTaxValue())));
    }//GEN-LAST:event_btmCalculateActionPerformed

